  <span class="token keyword">const</span> <span class="token punctuation">[</span>child1Value<span class="token punctuation">,</span> setChild1Value<span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token function">useState</span><span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
