    <span class="token function">setChild2Value</span><span class="token punctuation">(</span>props<span class="token punctuation">.</span>value2 <span class="token operator">*</span> <span class="token number">3</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token punctuation">}</span><span class="token punctuation">,</span> <span class="token punctuation">[</span>props<span class="token punctuation">.</span>value2<span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
